import React, { useState, useEffect } from 'react';
import './App.css'; // Importe o arquivo de estilos

function App() {
  const [listsOfUniqueNumbers, setListsOfUniqueNumbers] = useState([]);

  const generateNumbers = () => {
    fetch('/api/lists')
      .then((response) => response.json())
      .then((data) => setListsOfUniqueNumbers(data))
      .catch((error) => console.error('Error fetching data:', error));
  };

  useEffect(() => {
    generateNumbers();
  }, []); // Executa uma vez no carregamento inicial

  return (
    <div className="App"> {/* Aplicando a classe App */}
      <h1>Listas com números aleatórios distintos:</h1>
      {listsOfUniqueNumbers.map((numbers, index) => (
        <div key={index}>
          <p>{`Lista ${index + 1}: ${numbers.join(', ')}`}</p>
        </div>
      ))}
      <button onClick={generateNumbers}>Gerar Novos Números</button>
    </div>
  );
}

export default App;
