import React, { useState, useEffect } from 'react';
import './App.css'; // Importe o arquivo de estilos

function App() {
  const [listsOfUniqueNumbers, setListsOfUniqueNumbers] = useState([]);

  const apiUrl = 'http://localhost:3001/mega-sena';

  const generateNumbers = () => {
    fetch(apiUrl)
      .then((response) => response.json())
      .then((data) => setListsOfUniqueNumbers(data))
      .catch((error) => console.error('Error fetching data:', error));
  };
	console.log('listsOfUniqueNumbers', listsOfUniqueNumbers);
	console.log('setListsOfUniqueNumbers', setListsOfUniqueNumbers);
  return (
    <div className="App">
      <h1>Listas com números aleatórios distintos:</h1>
      <button onClick={generateNumbers}>Gerar Números da Mega sena</button>
      {listsOfUniqueNumbers.map((numbers, index) => (
        <div key={index}>
          <p>{`Jogo ${index + 1}: ${numbers.join(', ')}`}</p>
        </div>
      ))}
    </div>
  );
}

export default App;
